var http = require('http');

module.exports = {

    getAppToken: function(callback) {
        http.request({
                host: 'graph.facebook.com',
                path: '/oauth/access_token?client_id=756388677788740&client_secret=72038ed42db939a15fd378991056c1a9&grant_type=client_credentials&redirect_uri=http%3A%2F%2Fwww.ukfc.com:3000',
                method: 'GET'
        }, callback)
    }
}
